import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { MDBDataTable } from 'mdbreact';
import { Card, CardBody, CardHeader, Col } from 'reactstrap';
import LoadingOverlay from 'react-loading-overlay';
import { Radio, Row, Typography, Button, Statistic, Select } from 'antd';
import { Chart, Tooltip, Axis, Point, SmoothLine } from 'viser-react';

const { Text } = Typography;
const { Option } = Select;

class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      token: null,
      revenue: [],
      loading: false
    };
  }

  componentWillMount = async () => {
    // const token = localStorage.removeItem("token");
    const token = JSON.parse(localStorage.getItem('token'));

    if (token) {
      this.setState({ token });
    } else {
      this.props.history.push('/login');
    }
  };

  componentDidMount() {
    const token = JSON.parse(localStorage.getItem('token'));
    if (token) this.setState({ token });
    this.getRevenue();
  }

  handleResponse(response) {
    return response.text().then(text => {
      const data = text && JSON.parse(text);
      if (!response.ok) {
        return false;
      }
      return data;
    });
  }

  getRevenue = async () => {
    const requestOptions = {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${this.state.token}`
      }
    };
    try {
      const response = await fetch(
        'https://cors-anywhere.herokuapp.com/https://admin-api-tutor.herokuapp.com/me/revenue',
        requestOptions
      );

      let revenue = await this.handleResponse(response);
      console.log(revenue);
      this.setState({ loading: false, revenue: revenue });
    } catch (err) {
      console.log(err);
    }
  };

  render() {
    let data = [];
    let sum = 0;
    if (this.state.revenue.length) {
      data = [];
      const revenue = this.state.revenue;
      revenue.forEach((item, index) => {
        data.push({ contract: (index + 1).toString(), value: item.price });
        sum = sum + item.price;
      });
      console.log(data);
    }

    const scale = [
      {
        dataKey: 'value'
      },
      {
        dataKey: 'contract'
      }
    ];

    return (
      <div className="animated fadeIn mt-4">
        <Row type="flex" justify="center" className="mt-4">
          <Col xl={5}>
            <Row type="flex" justify="space-around">
              <Statistic title="Revenue (VND)" value={sum} precision={0} />
              <Statistic title="Contracts" value={data.length} precision={0} />
            </Row>
          </Col>
          <Col xl={6}>
            <Chart forceFit height={400} data={data} scale={scale}>
              <Tooltip />
              <Axis />
              <SmoothLine position="contract*value" />
              <Point position="contract*value" shape="circle" />
            </Chart>
          </Col>
        </Row>
      </div>
    );
  }
}

export default Dashboard;
