import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { MDBDataTable } from 'mdbreact';
import { CardBody, CardHeader, Col } from 'reactstrap';
import LoadingOverlay from 'react-loading-overlay';
import { Card, Row, Typography, Button, Statistic, Select } from 'antd';
import {
  Chart,
  Tooltip,
  Axis,
  Point,
  SmoothLine,
  Legend,
  Sector,
  Coord
} from 'viser-react';

const { Text } = Typography;
const { Option } = Select;

class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      token: null,
      revenue: [],
      loading: false
    };
  }

  componentWillMount = async () => {
    // const token = localStorage.removeItem("token");
    const token = JSON.parse(localStorage.getItem('token'));

    if (token) {
      this.setState({ token });
    } else {
      this.props.history.push('/login');
    }
  };

  componentDidMount() {
    const token = JSON.parse(localStorage.getItem('token'));
    if (token) this.setState({ token });
    this.getRevenue();
  }

  handleResponse(response) {
    return response.text().then(text => {
      const data = text && JSON.parse(text);
      if (!response.ok) {
        return false;
      }
      return data;
    });
  }

  getRevenue = async () => {
    this.setState({ loading: true });
    const requestOptions = {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${this.state.token}`
      }
    };
    try {
      const response = await fetch(
        'https://cors-anywhere.herokuapp.com/https://admin-api-tutor.herokuapp.com/me/revenue',
        requestOptions
      );

      let revenue = await this.handleResponse(response);
      console.log(revenue);
      this.setState({ loading: false, revenue: revenue });
    } catch (err) {
      this.setState({ loading: false });
      console.log(err);
    }
  };

  render() {
    let data = [];
    let sum = 0;
    const skills = [
      { skill: '2001', revenue: 41.8 },
      { skill: '2002', revenue: 38 },
      { skill: '2003', revenue: 33.7 },
      { skill: '2004', revenue: 30.7 },
      { skill: '2005', revenue: 25.8 },
      { skill: '2006', revenue: 31.7 },
      { skill: '2007', revenue: 33 },
      { skill: '2008', revenue: 46 },
      { skill: '2009', revenue: 38.3 },
      { skill: '2010', revenue: 28 },
      { skill: '2011', revenue: 42.5 },
      { skill: '2012', revenue: 30.3 }
    ];
    if (this.state.revenue.length) {
      const revenue = this.state.revenue;
      revenue.forEach((item, index) => {
        data.push({ contract: (index + 1).toString(), value: item.price });
        sum = sum + item.price;
      });
      console.log(data);
    }

    const scale = [
      {
        dataKey: 'value'
      },
      {
        dataKey: 'contract'
      }
    ];

    return (
      <div className="animated fadeIn mt-4">
        <LoadingOverlay
          active={this.state.loading}
          spinner
          text="Loading..."
          styles={{
            overlay: base => ({
              ...base,
              background: 'rgba(255, 255, 255, 0.5)',
              color: 'black'
            }),
            spinner: base => ({
              ...base,
              width: '100px',
              '& svg circle': {
                stroke: 'rgba(255, 0, 0, 0.5)'
              }
            })
          }}
        >
          <Card title="Dashboard" bordered={false}>
            <Row type="flex" justify="center" className="mt-4">
              <Col xl={5}>
                <h3>Statistic</h3>
                <Row type="flex" justify="start">
                  <Chart forceFit height={350} data={skills}>
                    <Tooltip />
                    <Legend position="right" dataKey="skill" offsetX={-140} />
                    <Coord type="polar" />
                    <Sector
                      position="skill*revenue"
                      color="skill"
                      style={{ stroke: '#fff', lineWidth: 1 }}
                    />
                  </Chart>
                  <Row type="flex" justify="start">
                    <Statistic
                      title="Revenue (VND)"
                      value={sum}
                      precision={0}
                    />
                    <Statistic
                      title="Contracts"
                      value={data.length}
                      precision={0}
                      className="ml-4"
                    />
                  </Row>
                </Row>
              </Col>
              <Col xl={6}>
                <Card title="Revenue" bordered={true}>
                  <Chart forceFit height={400} data={data} scale={scale}>
                    <Tooltip />
                    <Axis />
                    <SmoothLine color="#00d77d" position="contract*value" />
                    <Point
                      color="#00d77d"
                      position="contract*value"
                      shape="circle"
                    />
                  </Chart>
                </Card>
              </Col>
            </Row>
          </Card>
        </LoadingOverlay>
      </div>
    );
  }
}

export default Dashboard;
