import { Modal, Tag } from 'antd';
import React, { Component } from 'react';

class MessageBox extends React.Component {
  state = { visible: false };

  componentDidMount() {
    console.log(this.props.messages);
  }

  showModal = () => {
    this.setState({
      visible: true
    });
  };

  render() {
    let messages = [];
    if (this.props.messages) messages = this.props.messages;
    return (
      <div>
        <Modal
          title="Basic Modal"
          visible={this.props.visible}
          onOk={this.props.handleCancel}
          onCancel={this.props.handleCancel}
        >
          {messages.message.map(item => {
            return (
              <div className="m-2">
                <div>
                  {item.author === this.props.student ? 'student' : 'tutor'}
                </div>
                <Tag
                  style={{ padding: '5px', fontSize: '14px' }}
                  color={
                    item.author === this.props.student ? '#00d77d' : '#d700c5'
                  }
                >
                  {item.data.text} {item.data.emoji}
                </Tag>
              </div>
            );
          })}
        </Modal>
      </div>
    );
  }
}

export default MessageBox;
