import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { MDBDataTable } from 'mdbreact';
import { Card, CardBody, CardHeader, Col } from 'reactstrap';
import LoadingOverlay from 'react-loading-overlay';
import { Radio, Row, Typography, Button, Tag, Select } from 'antd';
import { Chart, Tooltip, Axis, Point, SmoothLine } from 'viser-react';

const { Text } = Typography;
const { Option } = Select;

class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      token: null,
      revenue: [],
      loading: false
    };
  }

  componentWillMount = async () => {
    // const token = localStorage.removeItem("token");
    const token = JSON.parse(localStorage.getItem('token'));

    if (token) {
      this.setState({ token });
    } else {
      this.props.history.push('/login');
    }
  };

  componentDidMount() {
    const token = JSON.parse(localStorage.getItem('token'));
    if (token) this.setState({ token });
    this.getRevenue();
  }

  handleResponse(response) {
    return response.text().then(text => {
      const data = text && JSON.parse(text);
      if (!response.ok) {
        return false;
      }
      return data;
    });
  }

  getRevenue = async () => {
    const requestOptions = {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${this.state.token}`
      }
    };
    try {
      const response = await fetch(
        'https://cors-anywhere.herokuapp.com/https://admin-api-tutor.herokuapp.com/me/revenue',
        requestOptions
      );

      let revenue = await this.handleResponse(response);
      console.log(revenue);
      this.setState({ loading: false, revenue: revenue });
    } catch (err) {
      console.log(err);
    }
  };

  render() {
    const data = [
      { contract: '1991', value: 3 },
      { contract: '1992', value: 4 },
      { contract: '1993', value: 3.5 },
      { contract: '1994', value: 5 },
      { contract: '1995', value: 4.9 },
      { contract: '1996', value: 6 },
      { contract: '1997', value: 7 },
      { contract: '1998', value: 9 },
      { contract: '1999', value: 13 },
      { contract: '1991', value: 3 },
      { contract: '1992', value: 4 },
      { contract: '1993', value: 3.5 },
      { contract: '1994', value: 5 },
      { contract: '1995', value: 4.9 },
      { contract: '1996', value: 6 },
      { contract: '1997', value: 7 },
      { contract: '1998', value: 9 },
      { contract: '1999', value: 13 },
      { contract: '1991', value: 3 }
    ];
    if (this.state.revenue) {
      data = this.state.revenue;
    }

    const scale = [
      {
        dataKey: 'value',
        min: 0
      },
      {
        dataKey: 'contract',
        min: 0,
        max: 1
      }
    ];

    return (
      <div className="animated fadeIn mt-4">
        <Row>
          <Col xl={6}>
            <Chart forceFit height={400} data={data} scale={scale}>
              <Tooltip />
              <Axis />
              <SmoothLine position="contract*value" />
              <Point position="contract*value" shape="circle" />
            </Chart>
          </Col>
        </Row>
      </div>
    );
  }
}

export default Dashboard;
