import React, { Component, useReducer } from 'react';
import { Card, CardBody, CardHeader, Col, Row, Table } from 'reactstrap';
import { Badge, Button, Descriptions, Tag } from 'antd';
import LoadingOverlay from 'react-loading-overlay';

class Contract extends Component {
  constructor(props) {
    super(props);
    this.state = {
      token: null,
      loading: true,
      banAction: false,
      contractData: [],
      token: null,
      id: null
    };
  }

  componentWillMount() {
    const { id } = this.props.match.params;
    this.setState({ id });
  }

  componentDidMount = async () => {
    const token = JSON.parse(localStorage.getItem('token'));
    if (token) this.setState({ token });
    await this.getContract(token);
    let student = null;
    let banAction = false;
  };

  getBadge = status => {
    return status === 'active'
      ? 'success'
      : status === 'inactive'
      ? 'default'
      : status === 'banned'
      ? 'error'
      : 'primary';
  };

  getContract = async token => {
    this.setState({ loading: true });
    const requestOptions = {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`
      }
    };
    try {
      const response = await fetch(
        `https://cors-anywhere.herokuapp.com/https://admin-api-tutor.herokuapp.com/me/contracts/${this.state.id}`,
        requestOptions
      );

      let contractData = await this.handleResponse(response);
      console.log(contractData);
      this.setState({ loading: false, contractData: contractData });
    } catch (err) {
      return err;
    }
  };

  handleResponse(response) {
    return response.text().then(text => {
      const data = text && JSON.parse(text);
      if (!response.ok) {
        if (response.status === 401) {
          // auto logout if 401 response returned from api
          // logoutUser();
        }

        // const error = (data && data.message) || response.statusText;
        // return Promise.reject(error);
        return data;
      }
      return data;
    });
  }

  banUserAction = async () => {
    this.setState({ loading: true });
    const requestOptions = {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${this.state.token}`
      }
    };
    try {
      const response = await fetch(
        `https://cors-anywhere.herokuapp.com/https://admin-api-tutor.herokuapp.com/me/ban-student/${this.state.student._id}`,
        requestOptions
      );

      const data = await this.handleResponse(response);
      let student = this.state.student;
      const date = new Date(student.date);
      student.date = date.toLocaleString();
      student.status = <Badge status={this.getBadge(data)} text={data}></Badge>;

      this.setState({
        loading: false,
        student,
        banAction: !this.state.banAction
      });
    } catch (err) {
      console.log(err);
    }
  };

  render() {
    const colors = [
      'magenta',
      'cyan',
      'green',
      'volcano',
      'orange',
      'purple',
      'lime',
      'red',
      'blue',
      'gold',
      'geekblue'
    ];
    let { student } = this.state.contractData;
    console.log(student);

    let skills = [];
    if (student) {
      let count = -1;
      student.skills.map(item => {
        const color = colors[++count % colors.length];
        const skillTag = <Tag color={color}>{item}</Tag>;
        skills.push(skillTag);
      });
    }
    if (!student) {
      student = { id: 'Not found' };
    } else {
      student.address = Object.values(student.address).join(', ');
    }

    return (
      <div className="animated fadeIn">
        <LoadingOverlay
          active={this.state.loading}
          spinner
          text="Loading..."
          styles={{
            overlay: base => ({
              ...base,
              background: 'rgba(255, 255, 255, 0.5)',
              color: 'black'
            }),
            spinner: base => ({
              ...base,
              width: '100px',
              '& svg circle': {
                stroke: 'rgba(255, 0, 0, 0.5)'
              }
            })
          }}
        >
          <Row className="justify-content-center">
            <Col lg={12}>
              <Card>
                <CardHeader className="bg-dark">
                  <strong>
                    <i className="icon-info pr-1"></i>Contract ID:{' '}
                    {this.props.match.params.id}
                  </strong>
                </CardHeader>
                <CardBody>
                  <Row>
                    <Col lg={3} className=" mt-4">
                      <Row className="justify-content-center font-weight-bold mb-3">
                        <h2 className="text-uppercase name-font">
                          {student.name}
                        </h2>
                      </Row>
                      <Row className="justify-content-center">
                        {student.picture}
                      </Row>
                    </Col>
                    <Col className="ml-4 mt-4" lg={8}>
                      <Descriptions
                        title="User Information"
                        bordered
                        column={{ xxl: 4, xl: 3, lg: 3, md: 3, sm: 2, xs: 1 }}
                      >
                        <Descriptions.Item label="Role">
                          {student.role}
                        </Descriptions.Item>
                        <Descriptions.Item label="Status">
                          {student.status}
                        </Descriptions.Item>
                        <Descriptions.Item label="Registered">
                          {student.date}
                        </Descriptions.Item>
                        <Descriptions.Item label="Rating">
                          {student.rate}
                        </Descriptions.Item>
                        <Descriptions.Item label="Skills">
                          {student.skillCount}
                        </Descriptions.Item>
                        <Descriptions.Item label="Wages">
                          {student.wages}
                        </Descriptions.Item>
                        <Descriptions.Item label="Info">
                          <strong>Email: </strong>
                          <br />{' '}
                          <span className="ml-4 mb-2">{student.email}</span>
                          <br />
                          <strong>Address: </strong>
                          <br />{' '}
                          <span className="ml-4 mb-2">{student.address}</span>
                          <br /> <strong>Skills: </strong>
                          <br /> <span className="ml-4 mb-2">{skills}</span>
                          <br />
                          <strong>Introduction: </strong>
                          <br />
                          <span className="ml-4 mb-2">
                            {student.introduction}
                          </span>
                        </Descriptions.Item>
                      </Descriptions>
                      <Row className="justify-content-end mt-4 mr-2">
                        <Button
                          onClick={() => this.banUserAction()}
                          type={this.state.banAction ? 'primary' : 'danger'}
                        >
                          {this.state.banAction ? 'Unban' : 'Ban'}
                        </Button>
                      </Row>
                    </Col>
                  </Row>
                </CardBody>
              </Card>
            </Col>
          </Row>
        </LoadingOverlay>
      </div>
    );
  }
}

export default Contract;
