import React, { Component, useReducer } from 'react';
import { Card, CardBody, CardHeader, Col, Row, Table } from 'reactstrap';
import { Badge, Button, Descriptions, Tag } from 'antd';
import LoadingOverlay from 'react-loading-overlay';

class Contract extends Component {
  constructor(props) {
    super(props);
    this.state = {
      token: null,
      loading: true,
      banAction: false,
      contractData: [],
      token: null,
      id: null
    };
  }

  componentWillMount() {
    const { id } = this.props.match.params;
    this.setState({ id });
  }

  componentDidMount = async () => {
    const token = JSON.parse(localStorage.getItem('token'));
    if (token) this.setState({ token });
    await this.getContract(token);
    let student = null;
    let banAction = false;
  };

  getBadge = status => {
    return status === 'active'
      ? 'success'
      : status === 'inactive'
      ? 'default'
      : status === 'banned'
      ? 'error'
      : 'primary';
  };

  getContract = async token => {
    this.setState({ loading: true });
    const requestOptions = {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`
      }
    };
    try {
      const response = await fetch(
        `https://cors-anywhere.herokuapp.com/https://admin-api-tutor.herokuapp.com/me/contracts/${this.state.id}`,
        requestOptions
      );

      let contractData = await this.handleResponse(response);
      this.setState({ loading: false, contractData: contractData });
    } catch (err) {
      return err;
    }
  };

  async handleResponse(response) {
    return response.text().then(text => {
      const data = text && JSON.parse(text);
      if (!response.ok) {
        if (response.status === 401) {
          // auto logout if 401 response returned from api
          // logoutUser();
        }

        // const error = (data && data.message) || response.statusText;
        // return Promise.reject(error);
        return data;
      }
      return data;
    });
  }

  getBadge = status => {
    return status === 'active'
      ? 'success'
      : status === 'inactive'
      ? 'default'
      : status === 'banned'
      ? 'error'
      : 'primary';
  };

  render() {
    const colors = [
      'magenta',
      'cyan',
      'green',
      'volcano',
      'orange',
      'purple',
      'lime',
      'red',
      'blue',
      'gold',
      'geekblue'
    ];
    let { student, tutor, contract } = this.state.contractData;
    let skills = [];
    let tutorSkills = [];
    let date;
    if (student) {
      let count = -1;
      student.skills.map(item => {
        const color = colors[++count % colors.length];
        const skillTag = <Tag color={color}>{item}</Tag>;
        skills.push(skillTag);
      });
    }
    if (tutor) {
      let count = -1;
      tutor.skills.map(item => {
        const color = colors[++count % colors.length];
        const skillTag = <Tag color={color}>{item}</Tag>;
        tutorSkills.push(skillTag);
      });
    }
    if (!student) {
      student = { id: 'Not found' };
    } else {
      student.address = Object.values(student.address).join(', ');
      student.picture = (
        <img
          src={student.picture}
          style={{
            height: 200,
            width: 200,
            objectFit: 'cover',
            borderRadius: 125
          }}
          alt="profile"
        />
      );
      student.status = (
        <Badge
          status={this.getBadge(student.status)}
          text={student.status}
        ></Badge>
      );
    }

    if (!tutor) {
      tutor = { id: 'Not found' };
    } else {
      tutor.address = Object.values(tutor.address).join(', ');
      tutor.picture = (
        <img
          src={tutor.picture}
          style={{
            height: 200,
            width: 200,
            objectFit: 'cover',
            borderRadius: 125
          }}
          alt="profile"
        />
      );
      tutor.status = (
        <Badge status={this.getBadge(tutor.status)} text={tutor.status}></Badge>
      );
    }
    if (!contract) {
      contract = { id: 'Not found' };
    } else {
      date = new Date(contract.contractCreationDate);
      date = date.getMonth() + date.getDate() + date.getFullYear();
      console.log(datex);
    }

    return (
      <div className="animated fadeIn">
        <LoadingOverlay
          active={this.state.loading}
          spinner
          text="Loading..."
          styles={{
            overlay: base => ({
              ...base,
              background: 'rgba(255, 255, 255, 0.5)',
              color: 'black'
            }),
            spinner: base => ({
              ...base,
              width: '100px',
              '& svg circle': {
                stroke: 'rgba(255, 0, 0, 0.5)'
              }
            })
          }}
        >
          <Row className="justify-content-center">
            <Col lg={12}>
              <Card>
                <CardHeader className="bg-dark">
                  <strong>
                    <i className="icon-info pr-1"></i>Contract ID:{' '}
                    {this.props.match.params.id}
                  </strong>
                </CardHeader>
                <CardBody>
                  <div
                    style={{ border: 'solid 1px grey', paddingBottom: '20px' }}
                  >
                    <Row className="justify-content-center mt-4">
                      <h1 className="text-uppercase font-weight-bold">
                        Contract
                      </h1>
                    </Row>
                    <Row className="justify-content-center mb-4">
                      <h3 className="">
                        status: {contract ? contract.currentStatus : null}
                      </h3>
                    </Row>
                    <Row className="justify-content-end mb-4">
                      <h4 style={{ paddingRight: '100px' }}>
                        {contract ? contract.contractCreationDate : null}
                      </h4>
                    </Row>
                    <Row>
                      <Col lg={3} className=" mt-4">
                        <Row className="justify-content-center font-weight-bold mb-3">
                          <h2 className="text-uppercase name-font">
                            {student.name}
                          </h2>
                        </Row>
                        <Row className="justify-content-center">
